        for (let i = 0; i < len; i++) {
            for (let j = 0; j < len - i - 1; j++) {
                bars[j].style.backgroundColor = 'orange'; // Highlight current comparison
                bars[j + 1].style.backgroundColor = 'orange'; // Highlight current comparison

                await new Promise(resolve => setTimeout(resolve, 500)); // Delay for visualization

                if (array[j] > array[j + 1]) {
                    // Swap
                    [array[j], array[j + 1]] = [array[j + 1], array[j]];
                    bars[j].style.height = `${array[j] * 20}px`;
                    bars[j + 1].style.height = `${array[j + 1] * 20}px`;
                }

                bars[j].style.backgroundColor = 'teal'; // Reset color
                bars[j + 1].style.backgroundColor = 'teal'; // Reset color
            }
            bars[len - i - 1].classList.add('sorted'); // Mark the last sorted item
        }
    };

    const resetArray = () => {
        array = [5, 3, 8, 4, 2];
        createBars();
    };

    document.getElementById('sortBtn').addEventListener('click', () => {
        createBars();
        bubbleSort();
    });

    document.getElementById('resetBtn').addEventListener('click', resetArray);

    // Initial display
    resetArray();
</script>

