<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Bubble Sort Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            background-color: #f4f4f4;
        }
        #array-container {
            display: flex;
            align-items: flex-end;
            margin-bottom: 20px;
        }
        .bar {
            background-color: #4CAF50;
            margin: 0 2px;
            width: 20px;
            display: inline-block;
        }
        button {
            padding: 10px 20px;
            font-size: 16px;
        }
    </style>
</head>
<body>

<div id="array-container"></div>
<button id="sort-button">Start Bubble Sort</button>

<script>
    const array = [5, 3, 8, 4, 2];
    const arrayContainer = document.getElementById('array-container');

    function createBars(arr) {
        arrayContainer.innerHTML = '';
        arr.forEach(value => {
            const bar = document.createElement('div');
            bar.className = 'bar';
            bar.style.height = value * 20 + 'px'; // Scale bar height
            arrayContainer.appendChild(bar);
        });
    }

    async function bubbleSort(arr) {
        const len = arr.length;
        for (let i = 0; i < len - 1; i++) {
            for (let j = 0; j < len - i - 1; j++) {
                // Update the visual
                createBars(arr);
                await new Promise(resolve => setTimeout(resolve, 500)); // Pause for visualization

                if (arr[j] > arr[j + 1]) {
                    // Swap
                    [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
                }
            }
        }
        // Final visualization
        createBars(arr);
    }

    document.getElementById('sort-button').addEventListener('click', () => {
        bubbleSort(array.slice()); // Pass a copy to sort
    });

    // Initial draw
    createBars(array);
</script>

</body>
</html>
