<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Bubble Sort Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            margin-top: 50px;
        }
        #array {
            display: flex;
            align-items: flex-end;
            margin-bottom: 20px;
        }
        .bar {
            width: 30px;
            margin: 1px;
            background-color: teal;
            transition: height 0.3s, background-color 0.3s;
        }
        .swapping {
            background-color: red;
        }
    </style>
</head>
<body>
    <h1>Bubble Sort Visualization</h1>
    <div id="array"></div>
    <button id="sortButton">Start Bubble Sort</button>

    <script>
        const array = [5, 1, 4, 2, 8, 3];
        const arrayContainer = document.getElementById('array');

        function drawArray(arr) {
            arrayContainer.innerHTML = '';
            arr.forEach(value => {
                const bar = document.createElement('div');
                bar.className = 'bar';
                bar.style.height = `${value * 20}px`;
                arrayContainer.appendChild(bar);
            });
        }

        async function bubbleSort(arr) {
            const n = arr.length;
            for (let i = 0; i < n - 1; i++) {
                for (let j = 0; j < n - i - 1; j++) {
                    // Highlight the bars to be compared
                    const bars = document.querySelectorAll('.bar');
                    bars[j].classList.add('swapping');
                    bars[j + 1].classList.add('swapping');

                    if (arr[j] > arr[j + 1]) {
                        // Swap the elements
                        [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
                    }

                    drawArray(arr);
                    await new Promise(resolve => setTimeout(resolve, 500)); // delay for visualization

                    // Reset the highlighting
                    bars[j].classList.remove('swapping');
                    bars[j + 1].classList.remove('swapping');
                }
            }
        }

        document.getElementById('sortButton').addEventListener('click', () => {
            drawArray(array); // Draw initial array
            bubbleSort(array.slice()); // Sort a copy of the array to preserve original
        });

        // Initial draw
        drawArray(array);
    </script>
</body>
</html>
