const randomArray = () => Array.from({length: 10}, () => Math.floor(Math.random() * 100) + 1);
        let array = randomArray();

        const renderArray = () => {
            const visualization = document.getElementById('array-visualization');
            visualization.innerHTML = '';
            array.forEach(num => {
                const bar = document.createElement('div');
                bar.className = 'bar';
                bar.style.height = `${num * 2}px`;
                visualization.appendChild(bar);
            });
        };

        const bubbleSort = async () => {
            const n = array.length;
            for (let i = 0; i < n - 1; i++) {
                for (let j = 0; j < n - i - 1; j++) {
                    if (array[j] > array[j + 1]) {
                        // Swap
                        [array[j], array[j + 1]] = [array[j + 1], array[j]];
                        renderArray();
                        await new Promise(resolve => setTimeout(resolve, 500)); // Delay for visualization
                    }
                }
            }
            alert('Sorting complete!');
            document.getElementById('start-button').disabled = false; // Re-enable button
        };

        const startBubbleSort = () => {
            document.getElementById('start-button').disabled = true; // Disable button during sort
            bubbleSort();
        };

        const nextModule = () => {
            document.querySelector('.module:not([style*="display: none"])').style.display = 'none';
            document.querySelector('.module[style="display:none;"]').style.display = 'block';
            renderArray();
        };

        renderArray(); // Initial render

