<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Sort Demo</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 20px;
        }
        input, button {
            margin-top: 10px;
            padding: 10px;
            font-size: 16px;
        }
        #result {
            margin-top: 20px;
            font-weight: bold;
        }
    </style>
</head>
<body>

<h1>Simple Sort Demo</h1>
<p>Enter numbers separated by commas (e.g. 5,3,8,1):</p>
<input type="text" id="numbers" placeholder="Enter numbers here...">
<button onclick="sortNumbers()">Sort Numbers</button>

<div id="result"></div>

<script>
    function sortNumbers() {
        const input = document.getElementById('numbers').value;
        const numberArray = input.split(',').map(num => parseFloat(num.trim()));

        // Check for valid numbers
        if (numberArray.some(isNaN)) {
            document.getElementById('result').innerText = "Please enter valid numbers.";
            return;
        }

        // Sort numbers
        numberArray.sort((a, b) => a - b);

        // Display sorted numbers
        document.getElementById('result').innerText = "Sorted Numbers: " + numberArray.join(', ');
    }
</script>

</body>
</html>
