        }
        .container {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        .learning-objective, .interaction-wrapper {
            margin: 16px;
            padding: 16px;
            border: 1px solid #ccc;
            border-radius: 8px;
            background-color: #fff;
            width: 100%;
            max-width: 600px;
        }
        .input-wrapper {
            display: flex;
            margin-top: 16px;
        }
