            padding: 8px;
            border: 1px solid #ccc;
            border-radius: 4px;
            flex-grow: 1;
            margin-right: 8px;
        }
        .input-wrapper button {
            padding: 8px 16px;
            background-color: #1a73e8;
            border: none;
            color: white;
            border-radius: 4px;
            cursor: pointer;
        }
        #output {
            margin-top: 16px;
            padding: 12px;
            border: 1px solid #cccccc;
            border-radius: 4px;
            word-wrap: break-word;
            background-color: #e9f5ff;
            transition: ease-in-out 0.3s;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>Interactive Hash Map Explorer</h1>
        <div class="learning-objective">
            <h2>Learning Objective</h2>
            <p>Understand how Hash Maps work by adding, retrieving, and removing key-value pairs interactively.</p>
        </div>

        <div class="interaction-wrapper">
            <h3>Hash Map Operations</h3>
            <div class="input-wrapper">
                <input type="text" id="key" placeholder="Enter a key..." />
                <input type="text" id="value" placeholder="Enter a value..." />
                <button id="add-button">Add Entry</button>
            </div>
            <button id="remove-button">Remove Entry</button>
            <hr />
            <div id="output"></div>
        </div>
    </div>

    <script>
        const hashMap = {};
        const outputDiv = document.getElementById("output");
        const addButton = document.getElementById("add-button");
        const removeButton = document.getElementById("remove-button");

        addButton.addEventListener("click", function() {
            const key = document.getElementById("key").value.trim();
            const value = document.getElementById("value").value.trim();

            if (key && value) {
                hashMap[key] = value;
                outputDiv.innerHTML = `<strong>Entry Added:</strong> Key: ${key}, Value: ${value}<br><br>${displayHashMap()}`;
                clearInputs();
                fadeEffect();
            } else {
                alert("Please specify both key and value.");
            }
        });

        removeButton.addEventListener("click", function() {
            const key = document.getElementById("key").value.trim();
            if (hashMap[key]) {
                delete hashMap[key];
                outputDiv.innerHTML = `<strong>Entry Removed:</strong> Key: ${key}<br><br>${displayHashMap()}`;
                clearInputs();
                fadeEffect();
            } else {
                alert("Key not found. Please enter a valid key.");
            }
        });

        function displayHashMap() {
            return `<strong>Current Hash Map:</strong><br>${JSON.stringify(hashMap, null, 2)}`;
        }

        function clearInputs() {
            document.getElementById("key").value = "";
            document.getElementById("value").value = "";
        }

        function fadeEffect() {
            outputDiv.style.opacity = 0;
            setTimeout(() => {
                outputDiv.style.opacity = 1;
            }, 300);
        }
    </script>
</body>
</html>
```

