            background: white;
            border-radius: 8px;
        }
        .module-title {
            font-size: 24px;
            margin-bottom: 8px;
        }
        .interactive-area {
            display: flex;
            gap: 16px;
            flex-wrap: wrap;
        }
        .button {
            padding: 10px 20px;
            background-color: #007bff;
            color: white;
            border: none;
            border-radius: 4px;
            cursor: pointer;
        }
        .sort-bar {
            width: 40px;
            background-color: #28a745;
            display: inline-block;
            margin: 0 4px;
            transition: height 0.3s;
